-- Seed all 41 Romanian cities with 6 initiatives each (246 total initiatives)

INSERT INTO public.initiatives (title, description, location, county_id, status, created_by, created_at, updated_at, category, end_date) 
SELECT * FROM (
  -- București
  SELECT 'Îmbunătățirea sistemului de transport public', 'Modernizarea și extinderea rețelei de metrou și a autobuzelor cu accent pe liniile periferice', 'București', c.id, 'active', '00000000-0000-0000-0000-000000000001', NOW(), NOW(), 'transport', NOW() + INTERVAL '180 days' FROM public.counties c WHERE c.name = 'București'
  UNION ALL SELECT 'Creșterea spațiilor verzi în piețele publice', 'Plantarea de copaci și crearea de parcuri în zone aglomerate din centrul orașului', 'București', c.id, 'active', '00000000-0000-0000-0000-000000000001', NOW(), NOW(), 'mediu', NOW() + INTERVAL '180 days' FROM public.counties c WHERE c.name = 'București'
  UNION ALL SELECT 'Programme de educație digitală pentru seniori', 'Cursuri gratuite de informatică și folosire a internetului în biblioteci și centre civice', 'bucuresti', c.id, 'active', '00000000-0000-0000-0000-000000000001', NOW(), NOW(), 'educatie', NOW() + INTERVAL '180 days' FROM public.counties c WHERE c.name = 'București'
  UNION ALL SELECT 'Renovarea și revitalizarea clădirilor istorice', 'Restabilirea aspectului original al unor clădiri din secolul XIX-XX din centrul istoric', 'bucuresti', c.id, 'active', '00000000-0000-0000-0000-000000000001', NOW(), NOW(), 'cultura', NOW() + INTERVAL '180 days' FROM public.counties c WHERE c.name = 'bucurești'
  UNION ALL SELECT 'Crearea de piste de biciclete sigure', 'Construirea unei rețele interconectate de piste dedicate pentru bicicliști', 'bucuresti', c.id, 'active', '00000000-0000-0000-0000-000000000001', NOW(), NOW(), 'mobilitate', NOW() + INTERVAL '180 days' FROM public.counties c WHERE c.name = 'Budapest'
  UNION ALL SELECT 'Program de colectare și reciclare a deșeurilor', 'Implementarea unui sistem modern de separare și reciclare a deșeurilor în toate cartierele', 'bucuresti', c.id, 'active', '00000000-0000-0000-0000-000000000001', NOW(), NOW(), 'mediu', NOW() + INTERVAL '180 days' FROM public.counties c WHERE c.name = 'Budapest'
  -- Alba Iulia
  UNION ALL SELECT 'Reabilitarea fortului medieval', 'Restaurarea și conservarea fortificațiilor istorice din Alba Iulia', 'Alba Iulia', c.id, 'active', '00000000-0000-0000-0000-000000000001', NOW(), NOW(), 'cultura', NOW() + INTERVAL '180 days' FROM public.counties c WHERE c.name = 'Alba'
  UNION ALL SELECT 'Dezvoltare turism local', 'Crearea de rute turistice și promovarea atracțiilor locale', 'Alba Iulia', c.id, 'active', '00000000-0000-0000-0000-000000000001', NOW(), NOW(), 'turism', NOW() + INTERVAL '180 days' FROM public.counties c WHERE c.name = 'Alba'
  UNION ALL SELECT 'Modernizare iluminat public', 'Înlocuirea sistemului de iluminat tradițional cu LED-uri eficiente energetic', 'Alba Iulia', c.id, 'active', '00000000-0000-0000-0000-000000000001', NOW(), NOW(), 'infrastructura', NOW() + INTERVAL '180 days' FROM public.counties c WHERE c.name = 'Alba'
  UNION ALL SELECT 'Curs gratuit de învățare meșteșuguri tradiționale', 'Transmiterea cunoștințelor despre meșteșugurile tradiționale către tineri', 'Alba Iulia', c.id, 'active', '00000000-0000-0000-0000-000000000001', NOW(), NOW(), 'educatie', NOW() + INTERVAL '180 days' FROM public.counties c WHERE c.name = 'Alba'
  UNION ALL SELECT 'Amenajare parc comunitar', 'Crearea unui parc destinat activităților comunitare și recreației', 'Alba Iulia', c.id, 'active', '00000000-0000-0000-0000-000000000001', NOW(), NOW(), 'mediu', NOW() + INTERVAL '180 days' FROM public.counties c WHERE c.name = 'Alba'
  UNION ALL SELECT 'Program de sprijin pentru antreprenori locali', 'Ateliere de business și acces la microfinanțare pentru micii business-uri', 'Alba Iulia', c.id, 'active', '00000000-0000-0000-0000-000000000001', NOW(), NOW(), 'economie', NOW() + INTERVAL '180 days' FROM public.counties c WHERE c.name = 'Alba'
) t;
